var HSJ={};HSJ.T=function(t){var t_;if(t==0){t_=BootstrapDialog.TYPE_DEFAULT}else{if(t==1){t_=BootstrapDialog.TYPE_INFO}else{if(t==2){t_=BootstrapDialog.TYPE_PRIMARY}else{if(t==3){t_=BootstrapDialog.TYPE_SUCCESS}else{if(t==4){t_=BootstrapDialog.TYPE_WARNING}else{if(t==5){t_=BootstrapDialog.TYPE_DANGER}else{t_=BootstrapDialog.TYPE_DEFAULT}}}}}}return t_};HSJ.S=function(s){var s_;if(s==1){s_=BootstrapDialog.SIZE_SMALL}else{if(s==2){s_=BootstrapDialog.SIZE_NORMAL}else{if(s==3){s_=BootstrapDialog.SIZE_LARGE}else{if(s==4){s_=BootstrapDialog.SIZE_WIDE}else{s_=BootstrapDialog.SIZE_NORMAL}}}}return s_};HSJ.util={msg:{base:function(m,t,s,c,r,f){var d=BootstrapDialog.show({type:HSJ.T(t),title:"温馨提示",message:m,size:HSJ.S(s),size:BootstrapDialog.SIZE_SMALL});setTimeout(function(){d.close();if(f!=null){f()}if(r!=null){window.location.href=r}},c)},common:function(data){if(data.status=="success"){HSJ.util.msg.base(data.message,3,2,3000,data.uri,data.callback)}else{if(data.status=="error"){HSJ.util.msg.base(data.message,5,2,3000,data.uri,data.callback)}else{if(data.status=="warn"){HSJ.util.msg.base(data.message,4,2,3000,data.uri,data.callback)}else{HSJ.util.msg.base(data.message,3,2,3000,data.uri,data.callback)}}}}},confirm:{base:function(title,msg,t,s,btns){var d=BootstrapDialog.show({type:HSJ.T(t),title:title,message:msg,size:HSJ.S(s),closable:false,buttons:btns})},common:{base:function(data){var btns=[{label:" 确 定 ",action:function(dialog){if(data.ok_callback!=null){data.ok_callback()}dialog.close()}},{label:" 取 消 ",action:function(dialog){if(data.cancel_callback!=null){data.cancel_callback()}dialog.close()}}];HSJ.util.confirm.base(data.title,data.message,data.t,data.s,btns)},suc:function(data){HSJ.util.confirm.common.base({title:"温馨提示",message:data.message,t:3,s:2,ok_callback:data.ok_callback,cancel_callback:data.cancel_callback})},err:function(data){HSJ.util.confirm.common.base({title:"温馨提示",message:data.message,t:5,s:2,ok_callback:data.ok_callback,cancel_callback:data.cancel_callback})},warn:function(data){HSJ.util.confirm.common.base({title:"温馨提示",message:data.message,t:4,s:2,ok_callback:data.ok_callback,cancel_callback:data.cancel_callback})}}}};